    public static double[] codificar(String src, String dst, int tamaño, boolean error){

        int[] module;
        int[] hamModule;
        int bitsControl = Decodificacion.controlBits(tamaño);
        double[] sizes = new double[2];

        try {
            // Archivo de lectura
            File fileR = new File(src);
            FileInputStream fr = new FileInputStream(fileR);
            BufferedInputStream br = new BufferedInputStream(fr);

            // Archivo de escritura
            File fileW = new File(dst);
            fileW.createNewFile();
            FileOutputStream fw = new FileOutputStream(fileW,true);
            BufferedOutputStream bw = new BufferedOutputStream(fw);

            module = LecturaArchivo.leerBits(br, (short) (tamaño-bitsControl));

            // reemplazar con do while y el primer read afuera.
            while(!isEmpty(module)) {

                hamModule = Codificacion.hamming(module, (short) tamaño);

                if(error) {
                    hamModule = Error.insertarError(hamModule, tamaño);
                }

                EscrituraArchivo.escribirInt(bw, hamModule);

                module = LecturaArchivo.leerBits(br, (short) (tamaño-bitsControl));

            }

            bw.close();
            br.close();

            // Tamaños para imprimir en bytes
            sizes[0] = fileR.length();
            sizes[1] = fileW.length();
        }
        catch(IOException e){
            System.out.println("Error en archivo");
        }
        return sizes;
    }

    // ModuleSize indica el tamaño del módulo ya hamminizado (32, 128, etc).
    public static int[] hamming(int[] vInfo, short moduleSize){
        //Iteradores
        int i = 0, c = 0;
        // Indices para los vectores de resultado y de información
        int indexRes = 0, indexInf = 0;
        // Mascaras para trabajo de bit
        int mascParidad = 1, mascInfo = 1;
        // Variables auxiliares
        int aux = 0;
        int contParidad = 0;

        int[] resIntermedio = new int[vInfo.length];
        // Vector B de resultado hamminizado
        int[] vRes = new int[vInfo.length];

        // Inicializo las máscaras
        mascParidad = mascParidad << 31;
        mascInfo = mascInfo << 31;

        // Dependiendo del tamaño del módulo traigo la matriz generadora correspondiente
        int[][] gen = Codificacion.obtenerMatriz(moduleSize);

        // Itero en la cantidad de bits que voy a tener de salida
        for(i = 0; i<moduleSize; i++){

            // Si estoy calculando un bit de control
            if((Math.pow(2,c)-1) == i){

                for (int k=0; k<vInfo.length; k++){
                    resIntermedio[k] = vInfo[k] & gen[c][k];
                    contParidad += parity(resIntermedio[k]);
                }
                // Si es paridad impar (bi = 1) lo modifico en vRes
                if(contParidad%2 == 1){
                   vRes[indexRes] = vRes[indexRes] | mascParidad;
                   contParidad = 0;
                }
                // Próximo bit de control
                c++;
            }
            // Sino copio el bit de información tal cual está
            else{
                // Extraigo el bit
                aux = vInfo[indexInf] & mascInfo;
                // Comparo si es 0 o 1 el bit extraído
                if(aux != 0){
                    // Si es un 1, entonces lo agrego al vRes, sino ignoro porque ya es 0
                    vRes[indexRes] = vRes[indexRes] | mascParidad;
                }
                // Avanzo un bit la máscara de info (entrada)
                mascInfo = mascInfo >>> 1;
            }

            // Avanzo un bit la máscara de paridad (salida)
            mascParidad = mascParidad >>> 1;

            // Control de reseteo de máscaras
            if (mascInfo == 0){
                mascInfo = 1;
                mascInfo = mascInfo << 31;
                indexInf++;
            }
            if(mascParidad == 0){
                mascParidad = 1;
                mascParidad = mascParidad << 31;
                indexRes++;
            }

        }

        return vRes;
    }

    // Comprueba la paridad
    public static int parity(int vector){
        int i;
        int cont = 0;

        for( i = 0; i<32; i++){
            cont += (vector & 1);
            vector = vector >> 1;

        }

        return cont%2;


    }

    public static boolean isEmpty(int[] module) {
        int bytes = Hamming.LecturaArchivo.getBytes();
        boolean emptyModule = true;
        for(int i=0; i<module.length; i++){
            if(module[i] != 0 )
                emptyModule = false;
        }
        if (bytes < 0 && emptyModule)
            return true;
        else
            return false;
    }


    public static int[][] obtenerMatriz(int moduleSize){

        switch (moduleSize){
            case 32: return gen32;
            case 128: return gen128;
            case 1024: return gen1024;
            case 4096: return gen4096;
            case 16384: return  gen16384;
            default: return null;
        }
    }
